import { Component, OnInit } from '@angular/core';
import { UsuarioService } from '../usuario-service.service';
import { Serie } from '../serie';
import { SerieEmpezada } from '../serieEmpezada';
import { Usuario } from '../usuario';

@Component({
  selector: 'app-visualizar-serie',
  templateUrl: './visualizar-serie.component.html',
  styleUrls: ['./visualizar-serie.component.css']
})
export class VisualizarSerieComponent implements OnInit {

  public usuario : any;
  private usuarioUrl: string = '';
  private idSerie: any;
  public pendiente: boolean = false;
  public serie: any;
  public temporada : any;

  constructor(public usuarioService: UsuarioService) {
    this.idSerie = window.location.href.split("/")[4];
    this.usuario = {};
  }

  ngOnInit(): void {
    this.usuario = this.usuarioService.usuario;
    var self = this;
    this.usuario.repertorioUsuario.seriesPendientes.forEach( function(valor:Serie, indice: number, array: Array<Serie>) {
      if(valor.idSerie == self.idSerie){
        self.pendiente = true;
        self.serie = valor;
        self.temporada = 0;
      }
    });

    this.buscarSerie(self);

  }

  buscarSerie(self: any){
    this.usuario.repertorioUsuario.seriesEmpezadas.forEach( function(valor:SerieEmpezada, indice: number, array: Array<SerieEmpezada>) {
      if(valor.serie.idSerie == self.idSerie){
        self.serie = valor;
        self.pendiente = false;
        self.temporada = valor.ultimaTemporadaVista-1;
      }
    });

    this.usuario.repertorioUsuario.seriesFinalizadas.forEach( function(valor:SerieEmpezada, indice: number, array: Array<SerieEmpezada>) {
      if(valor.serie.idSerie == self.idSerie){
        self.serie = valor;
        self.pendiente = false;
        self.temporada = valor.ultimaTemporadaVista-1;
      }
    });
  }

  async verCapitulo(numCapitulo:number){
    var self = this;
    if(this.pendiente == true){
      self.usuario = await this.usuarioService.verCapitulo(this.usuario.nombre, this.serie.idSerie,
        this.serie.temporadas[this.temporada].numTemporada, numCapitulo).toPromise();
    }else{
      self.usuario = await this.usuarioService.verCapitulo(this.usuario.nombre, this.serie.serie.idSerie,
        this.serie.serie.temporadas[this.temporada].numTemporada, numCapitulo).toPromise();

    }
    this.buscarSerie(this);
  }

  ngOnDestroy() {
    this.usuarioService.usuario = this.usuario;
  }

  restaTemporada(){
    if(this.temporada>0){
      this.temporada -= 1;
    }
  }

  sumaTemporada(){
    if(this.pendiente){
      if(this.serie.temporadas.length - 1 > this.temporada){
          this.temporada += 1;
      }
    }else{
      if(this.serie.serie.temporadas.length -1 > this.temporada){
          this.temporada += 1;
      }
    }
  }

}
<div class="month">
  <ul>
    <li class="prev"> <input class="button" type="submit" value="<<" (click)="restaTemporada()"> </li>
    <li>
      {{ pendiente ? serie.nombreSerie + " - Temporada " + serie.temporadas[temporada].numTemporada : serie.serie.nombreSerie + " - Temporada " + serie.serie.temporadas[temporada].numTemporada }}<br>
    </li>
    <li class="next"> <input class="button" type="submit" value=">>" (click)="sumaTemporada()"> </li>

  </ul>
</div>

<div *ngIf="pendiente == true; then thenBlock else elseBlock"></div>
<ng-template #thenBlock>

  <div>
    <label class="estandar">{{serie.categoria}}</label>
  </div>
<div>
  <table class="conjunto">
  <tbody>
          <tr *ngFor="let capitulo of serie.temporadas[temporada].capitulos">
            <td class="palabras">{{ capitulo.titulo }}</td>
            <td><input class="button" type="submit" value="Ver Capitulo" (click)="verCapitulo(capitulo.numero)"></td>
            <td class="palabras"> Pendiente </td>
          </tr>
  </tbody>

  </table>
</div>
</ng-template>

<ng-template #elseBlock>
  <div>
    <label class="estandar">{{serie.serie.categoria}}</label>
  </div>
  <table class="conjunto">
  <tbody>
          <tr *ngFor="let capitulo of serie.serie.temporadas[temporada].capitulos">
            <td class="palabras">{{ capitulo.titulo }}</td>
            <td><input class="button" type="submit" value="Ver Capitulo" (click)="verCapitulo(capitulo.numero)"></td>
            <div *ngIf="serie.ultimaTemporadaVista > serie.serie.temporadas[temporada].numTemporada ||
				(serie.ultimaTemporadaVista == serie.serie.temporadas[temporada].numTemporada &&
				serie.ultimoCapituloVisto >= capitulo.numero); then thenBlock else elseBlock"></div>
          <ng-template #thenBlock>
            <td class="palabras"> Visto </td>
          </ng-template>
          <ng-template #elseBlock>
            <td class="palabras"> Pendiente </td>
          </ng-template>
          </tr>
  </tbody>

  </table>

</ng-template>
